,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'-- Customer Retention Analysis',
'WITH customer_order_history AS (',
'    SELECT ',
'        customer_id,',
'        MIN(order_date) as first_order_date,',
'        MAX(order_date) as last_order_date,',
'        COUNT(DISTINCT order_id) as total_orders,',
'        MONTHS_BETWEEN(MAX(order_date), MIN(order_date)) as customer_tenure_months',
'    FROM ',
'        orders',
'    GROUP BY ',
'        customer_id',
')',
'SELECT ',
'    CASE ',
'        WHEN total_orders > 5 THEN ''Loyal Customer''',
'        WHEN total_orders > 2 THEN ''Repeat Customer''',
'        ELSE ''One-Time Customer''',
'    END as customer_type,',
'    COUNT(*) as customer_count,',
'    ROUND(COUNT(*) / (SELECT COUNT(DISTINCT customer_id) FROM customers) * 100, 2) as percentage,',
'    ROUND(AVG(customer_tenure_months), 2) as avg_customer_tenure',
'FROM ',
'    customer_order_history',
'GROUP BY ',
'    CASE ',
'        WHEN total_orders > 5 THEN ''Loyal Customer''',
'        WHEN total_orders > 2 THEN ''Repeat Customer''',
'        ELSE ''One-Time Customer''',
'    END;',
''))
